import { useState } from "react";
import mainbackground from "../assets/download.jpg";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import AddIcon from "@mui/icons-material/Add";
import { mobile } from "../Responsive";
import { useSelector } from "react-redux";
import { userRequest } from "../axiosReqMethods";
import { useNavigate } from "react-router-dom";


function ComplainRegister() {
    
  const user = useSelector((state) => state?.user?.currentUser);
  const userAddress = useSelector((state) => state.user.address);   
  
    const navigate = useNavigate();

  const handlepaidvisit = async() => {
   if (!user) {
     return navigate("/login");
   }

   // if there is address then continue or set get address popup
   if (!userAddress) {
     //if address is not stored in users local storage then get from db
     try {
       const { data } = await userRequest.get("/api/user/address");
       if (!data.ok) {
         return setaddmodalIsOpen(true);
       }
       dispatch(setAddress(data.address)); //setting address wh to redux
     } catch (error) {
       return setaddmodalIsOpen(true);
     }
   }

   if (!window.Razorpay) {
     await addDynamicScript("https://checkout.razorpay.com/v1/checkout.js"); //script is not loading at first time dk why so i added this XD
   }

   let Dborder, Dbkey;
   try {
     const {
       data: { order },
     } = await userRequest.post("api/buy/checkout", {
       user: user._id,
       product: {
         productID: product._id,
         quantity: ProductQuentity,
         size,
         color: Color,
       },
       type: "product",
       userInfo: {
         address: userAddress,
         name: `${user.firstName} ${user.lastName}`,
         email: user.email,
         number: user.number,
       },
     });
     Dborder = order;

     const {
       data: { key },
     } = await userRequest.get("api/buy/getkey");
     Dbkey = key;
   } catch (error) {
     dispatch(
       setError(
         error?.response?.data?.message || "error accured while creating order"
       )
     );
   }

   const options = {
     key: Dbkey, //reciving key from backend sue to security
     amount: Dborder.amount,
     currency: "INR",
     name: product.title,
     description: `${product.desc.slice(0, 252)}...` || "random description", //slicing it because razor pay dosent allow desc length more then 255
     image: product.img,
     order_id: Dborder.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
     callback_url: "http://localhost:4000/api/buy/paymentVerify",
     prefill: {
       name: `${user.firstName} ${user.lastName}`,
       email: user.email,
       contact: user.number,
     },
     notes: {
       address: "Dummy Office address",
     },
     theme: {
       color: "#40a0a0",
     },
   };
   const rzp1 = new window.Razorpay(options);
   rzp1.open();   
  
}
  

  const [data, setdata] = useState({
    lname: "",
    service: "",
    problemstatement: "",
    image: "",
      mobilenumber: "",
    email:"",
    name:""
  });
    const [check, setcheck] = useState("false");

  const handleInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setdata({
      ...data,
      [name]: value,
    });
    if (name === "image") {
      const file = e.target.files[0];
      const filereader = new FileReader();
      filereader.readAsDataURL(file);
      filereader.onload = () => {
        console.log("image changed on " )
        setdata(p => ({ ...p, img: filereader.result }))
      }
    }
    console.log(data);
  };

  const handleSubmit = async () => {
    console.log(data);
    try {
      console.log("mihir1");

      setcheck("true")

    } catch (error) {
      console.log(error);
    }
  };
  const handlecheck = async () => {
    console.log(data);
    try {
      console.log("mihir1");

      setcheck("true")

    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      <div style={{ backgroundImage: `url(${mainbackground})` }}>
        <Navbar />
        <div className="complain-container">
          <h1>Register Complain</h1>
          <div className="inputfield">
            <label>Enter Library Name </label>
            <input
              name="lname"
              onChange={handleInput}
              value={data.lname}
              required
              type="text"
            />
          </div>
          <div className="inputfield">
            <label>Enter Service name </label>
            <input
              name="service"
              onChange={handleInput}
              value={data.service}
              required
              type="text"
            />
          </div>
          {check === "true" && (
            <>
              <div className="inputfield">
                <label>Enter Your Name </label>
                <input
                  name="name"
                  onChange={handleInput}
                  value={data.name}
                  required
                  type="text"
                />
              </div>
              <div className="inputfield">
                <label>Enter Your Mobile Number </label>
                <input
                  name="mobilenumber"
                  onChange={handleInput}
                  value={data.mobilenumber}
                  required
                  type="number"
                />
              </div>
              <div className="inputfield">
                <label>Enter Your Email </label>
                <input
                  name="email"
                  onChange={handleInput}
                  value={data.email}
                  required
                  type="email"
                />
              </div>
              <div className="inputfield">
                <label>Write Your problem </label>
                <input
                  name="problemstatement"
                  onChange={handleInput}
                  value={data.problemstatement}
                  required
                  type="text"
                />
              </div>
              <div className="inputfield">
                <label>Upload Photo Of Error </label>
                <input
                  accept="image/jpeg, image/png"
                  name="image"
                  onChange={handleInput}
                  value={data.image}
                  type="file"
                />
              </div>
              <div className="inputfield">
                <button type="button" onClick={handlepaidvisit} className="btn ">
                  Submit
                </button>
              </div>
            </>
          )}

          {check === "false" && (
            <div className="inputfield">
              <button type="button" onClick={handlecheck} className="btn ">
                Check
              </button>
            </div>
          )}
        </div>

        <Footer />
      </div>
    </>
  );
}
export default ComplainRegister;
