import { useState } from "react";
import mainbackground from "../assets/download.jpg";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
function ComplainRegister() {
    
  const [data, setdata] = useState({
    lname: "",
    service: "",
    startdate: "",
    enddate: "",
    amount: "",
  });

  const handleInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setdata({
      ...data,
      [name]: value,
    });

    console.log(data);
  };

  const handleSubmit = async () => {
    console.log(data);
    try {
      console.log("mihir1");

      const res = await req.post(`/api/ticket`, data);

      console.log(res);
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      <div
        className="container"
        style={{ backgroundImage: `url(${mainbackground})` }}
      >
        <Navbar />
        <div className="maincontainer">
          <h1>Add Ordder</h1>
          <div className="inputfield">
            <label>Enter Library Name </label>
            <input
              name="lname"
              onChange={handleInput}
              value={data.lname}
              required
              type="text"
            />
          </div>
          <div className="inputfield">
            <label>Enter Service name </label>
            <input
              name="service"
              onChange={handleInput}
              value={data.service}
              required
              type="text"
            />
          </div>
          <div className="inputfield">
            <label>Start Date of service </label>
            <input
              name="startdate"
              onChange={handleInput}
              value={data.startdate}
              required
              type="date"
            />
          </div>
          <div className="inputfield">
            <label>End Date of service </label>
            <input
              name="enddate"
              onChange={handleInput}
              value={data.enddate}
              required
              type="date"
            />
          </div>
          <div className="inputfield">
            <label>Amount </label>
            <input
              name="amount"
              onChange={handleInput}
              value={data.amount}
              required
              type="number"
            />
          </div>
          <div className="inputfield">
            <button type="button" onClick={handleSubmit} className="btn ">
              Add <AddIcon />
            </button>
          </div>
        </div>

        <Footer />
      </div>
    </>
  );
}
export default ComplainRegister;
