import { useState } from "react";
import mainbackground from "../assets/download.jpg";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import AddIcon from "@mui/icons-material/Add";
import { mobile } from "../Responsive";
import { useSelector } from "react-redux";
import { userRequest } from "../axiosReqMethods";
import axios from "axios";


function ComplainRegister() {
    
  const user = useSelector((state) => state?.user?.currentUser);
  const handlepaidvisit = async() => {
   const {
     data: { order },
   } = await userRequest.post("api/buy/checkout", {
     user: user._id,
     type: "cart",
     userInfo: {
       address: library.lname,
       name: library.name,
       email: library.email,
     },
   });
    if (!window.Razorpay) {
      
      const script = await document.createElement("script");
      script.src = "https://checkout.razorpay.com/v1/checkout.js";
      await document.body.appendChild(script);
      console.log("script success to load");
    } 
   const {data:{key}} = await userRequest.get("api/buy/getkey");
const options = {
  key: key, //reciving key from backend for security purpose
  amount: 300000,
  currency: "INR",
  name: `${user.firstName} ${user.lastName}'s Cart`,
  description: `${user.firstName} ${user.lastName}'s Cart includes total`,
  image:
    "https://toppng.com/uploads/preview/astronaut-art-png-jpg-royalty-free-stock-astronauta-dibujo-11562856188offwkk8qo8.png",
  order_id: library.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
  // callback_url: "http://localhost:4000/api/buy/paymentVerify",
  prefill: {
    name: library.name,
    email: library.email,
    contact: library.mobilenumber,
  },
  notes: {
    address: "Razorpay Corporate Office",
  },
  theme: {
    color: "#40a0a0",
  },
};
    
    
const rzp1 = new window.Razorpay(options);
rzp1.open();
  
}
  

  const [library, setlibrary] = useState({
    lname: "",
    service: "",
    problemstatement: "",
    img: "",
      mobilenumber: "",
    email:"",
    name:""
  });
    const [check, setcheck] = useState("false");

  const handleInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setlibrary({
      ...library,
      [name]: value,
    });
    if (name === "image") {
      const file = e.target.files[0];
      const filereader = new FileReader();
      filereader.readAsDataURL(file);
      filereader.onload = () => {
        console.log("image changed on " )
        setlibrary((p) => ({ ...p, img: filereader.result }));
      }
    }
    console.log(library);
  };

  const handleSubmit = async () => {
    console.log(library);
    try {
      console.log("mihir1");
        const res = await userRequest.post("/api/complain", {
          library
        });
      // setcheck("true")
        console.log("done");
        
    } catch (error) {
      console.log(error);
    }
  };
  const handlecheck = async () => {
    console.log(library);
    try {
      console.log("mihir1");
      const res = await userRequest.get(`/api/ticket/${library.lname}`);
      console.log(res.data.enddate);

      // Date object
      const date = new Date();

      let currentDay = String(date.getDate()).padStart(2, "0");

      let currentMonth = String(date.getMonth() + 1).padStart(2, "0");

      let currentYear = date.getFullYear();

      // we will display the date as DD-MM-YYYY

      let currentDate = `${currentYear}-${currentMonth}-${currentDay}`;

      console.log("The current date is " + currentDate);

      lo
      if (res.data.enddate>=currentDate) {
        setcheck("true");
      }
      // setcheck("true")
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      <div
        className="container"
        style={{ backgroundImage: `url(${mainbackground})` }}
      >
        <Navbar />
        <div className="complain-container">
          <h1>Register Complain</h1>
          <div className="inputfield">
            <label>Enter Library Name </label>
            <input
              name="lname"
              onChange={handleInput}
              value={library.lname}
              required
              type="text"
            />
          </div>
          <div className="inputfield">
            <label>Enter Service name </label>
            <input
              name="service"
              onChange={handleInput}
              value={library.service}
              required
              type="text"
            />
          </div>
          {check === "true" && (
            <>
              <div className="inputfield">
                <label>Enter Your Name </label>
                <input
                  name="name"
                  onChange={handleInput}
                  value={library.name}
                  required
                  type="text"
                />
              </div>
              <div className="inputfield">
                <label>Enter Your Mobile Number </label>
                <input
                  name="mobilenumber"
                  onChange={handleInput}
                  value={library.mobilenumber}
                  required
                  type="number"
                />
              </div>
              <div className="inputfield">
                <label>Enter Your Email </label>
                <input
                  name="email"
                  onChange={handleInput}
                  value={library.email}
                  required
                  type="email"
                />
              </div>
              <div className="inputfield">
                <label>Write Your problem </label>
                <input
                  name="problemstatement"
                  onChange={handleInput}
                  value={library.problemstatement}
                  required
                  type="text"
                />
              </div>
              <div className="inputfield">
                <label>Upload Photo Of Error </label>
                <input
                  accept="image/jpeg, image/png"
                  name="image"
                  onChange={handleInput}
                  value={library.image}
                  type="file"
                />
              </div>
              <div className="inputfield">
                <button
                  type="button"
                  onClick={handleSubmit}
                  className="btn "
                >
                  Submit
                </button>
              </div>
            </>
          )}

          {check === "false" && (
            <div className="inputfield">
              <button type="button" onClick={handlecheck} className="btn ">
                Check
              </button>
            </div>
          )}
        </div>

        <Footer />
      </div>
    </>
  );
}
export default ComplainRegister;
