import React, { useEffect, useState}from 'react'
import Announcments from '../components/Announcments'
import Navbar from '../components/Navbar'
import styled from 'styled-components'
import NewsLetter from '../components/NewsLetter'
import Footer from '../components/Footer'
import { Add, ClearOutlined, Remove } from '@material-ui/icons'
import { mobile } from '../Responsive'
import  { useSelector, useDispatch } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { userRequest } from '../axiosReqMethods'
import { deleteProduct } from '../redux/cartRedux'
import addDynamicScript from '../helpers/addDynamicScript'
import { setError } from '../redux/errorRedux'
import EmptyCartComponent from '../components/EmptyCartComponent'
import Loading from '../components/Loading'
import { setAddress } from '../redux/userRedux'
import GetUserAddress from '../components/GetUserAddress'




const Container = styled.div``
const Wrapper = styled.div`
    padding: 20px;
`
const Title = styled.div`
    font-weight: 300;
    text-align: center;
`
const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`
const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    border: ${props=> props.type ==="filled" && "none"};
    background-color: ${props=> props.type ==="filled" ? "Black" : "transparent"};
    color: ${props=> props.type ==="filled" && "white"};
    
`
const TopTexts = styled.div`
    ${mobile({
        display: "none",
    })} 
`
const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 10px;
`

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
    //flex-direction: column;
    @media only screen and (max-width: 970px) {
        flex-direction: column;
        align-items: center;       
    }
`
const Info = styled.div`
    flex: 3;
    width: 100%;
    display: flex;
    flex-direction: column;
    @media only screen and (max-width: 970px) {
        align-items: center;       
    }

`



const Product = styled.div`
    flex: 2;
    display: flex;
    margin: 10px 0px;
    position: relative;
    overflow: hidden;
    background-color: #f7f7f7;
    margin-right: 20px;
    transition: all 0.3s ease-in-out;

    ${mobile({
        flexDirection: "column",  
        margin: "10px 5px",
    })} 
    @media only screen and (max-width: 970px) {
        width: 99%;
    }

    :hover{
      box-shadow: 0px 0px 5px #888888;
      transform: scale(1.02);
    }
`
const DelButton = styled.div`
    
    position: absolute;
    right: 0px;
    top: 0px;

    ${mobile({
        top: "45%"
    })}
    
`

const ProductDeteail = styled.div`
    flex: 2;
    width: 100%;
    display: flex;
    ${mobile({
        flex: "1",
        width: "100vw",
    })} 
`
const Image = styled.img`
    width: 200px;
    max-width: 30%;
    object-fit: cover;

`
const Details = styled.div`
    display: flex;
    flex: 1;
    flex-direction: column;
    max-width: fit-content;
    padding-right: 10px;
    justify-content: space-around;
    margin: 10px;
    ${mobile({
        width: "100%",
    })} 
    `
const ProductName = styled.span`
`
const ProductNumber = styled.span``
const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props => props.color};
`
const ProductSize = styled.span``
const PriceDeteail =styled.div`    
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    ${mobile({
        marginTop: "1rem",
        
    })} 
    
`


const ProductAmmountContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: 700;
    
    
`
const ValueARButton = styled.div`
    cursor: pointer;
    margin: 0 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    &:active {
        transform:scale(1.1);
     }
`

const ProductAmmount = styled.div`
    height: 30px;
    width: 30px; 
    border: solid 1px teal;
    padding :5px ;
    border-radius: 10%;
    display: flex;
    align-items: center;
    justify-content: center;
    
`
const ProductPrice = styled.span`
    margin: 20px 0px;
    font-weight: 600;
    font-size: 1.2rem;
    &:hover {
        transform: scale(1.1);
    }
`

const Hr = styled.hr`
    background-color: #eee;
    height: 1px;
    border: none;
`


const Summary = styled.div`
    flex: 1;
    max-height: 50vh;
    width: 350px;
    max-width: 100%;
    border: solid lightgray 1px;
    border-radius: 2vmax;
    padding: 10px;
    @media only screen and (max-width: 970px) {
        width: 90%;
    }
`

const SummaryTitle = styled.h1`
    font-weight: 300;
    margin: 10px 0px;
`
const SummaryItem = styled.div`
    display: flex;
    justify-content: space-between;
    margin: 5px 0px;
    font-weight: ${props=> props.type === "total" && 600};
    font-size: ${props=> props.type === "total" && 1.2}rem;
    margin: ${props=> props.type === "total" && 10}px 0px;
`
const SummaryText = styled.div`
    white-space: nowrap;
    width: 80%; 
    overflow: hidden;
`
const SummaryPrice = styled.div`
    
`
const ButtonWrapper = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`
const Button = styled.button`
    background-color: black;
    cursor: pointer;   
    color: white;
    border: none;
    padding: 20px;
    width: 80%;
    margin-top: 20px;

    :disabled {
        background-color: #6b6d70;
        cursor: not-allowed;
    }

    ${mobile({
        width: "50%",
        borderRadius: "5%"  ,   
        
    })} 
    
    
`

function CartPage(props) {
    const dispatch = useDispatch();
    const navigate = useNavigate()
    const [cartProductRes, setCartProductRes] = useState();
    const [fetchCartLoading, setFetchCartLoading] = useState();

    //to change title as soon as component mounts
    useEffect(() => {
        document.title = `PanaraIt - ${props.title}`
    }, []) // eslint-disable-line react-hooks/exhaustive-deps
    
    const user = useSelector(state => state?.user?.currentUser);


    //get User Cart
    useEffect( async () => {
        if(user) {
            try{      
                setFetchCartLoading(true)         
                const res = await userRequest.get(`/api/cart/info/${user._id}`)
                setFetchCartLoading(false)
                setCartProductRes(res.data)
                
            }catch(err){
                console.log("error", err)
                setFetchCartLoading(false)
                dispatch(setError(err.response.data.message))      //setting error       
            }           
        }
        return () => {
            setCartProductRes(null)
        }
    }, [])

    //count cart total
    const [totalCartPrice, setTotalCartPrice] = useState(0)
    useEffect(() => {   
        const total = cartProductRes?.products.reduce((total, item) => {
            return total + (item.price * item.quantity)
        },0)
        setTotalCartPrice(total);
    }, [cartProductRes?.products, cartProductRes?.products?.map(p => p.quantity)])//map is used bcz we need to reRender this component if any products quantity changes so we maped true every product quantity
     
    //delete product
    const handleDeleteProduct = async (id) => {
        try{
            const filteredProducts = cartProductRes?.products?.filter(p => {
                return id !== p.productID
            })
            setCartProductRes(e => ({...e, products: filteredProducts}))
            dispatch(deleteProduct())
            const res = await userRequest.delete(`/api/cart/${id}`)     
            dispatch(setError(res.data.message))
        }catch(error){
            console.log("error", error)
            dispatch(setError(error.response.data.message))
        }
    }

    //handle dec inc in product Quantity
    const handleProductQuantityChange = async (productID, quantity) => {

        if(quantity === 0 ) return handleDeleteProduct(productID)
        try {
            const res = await userRequest.put(`/api/cart/updatequantity/${productID}/${quantity}`)
            const productIndex = cartProductRes.products.findIndex(p => p.productID === productID)
            console.log({productIndex})
            const newProduct = cartProductRes.products[productIndex].quantity = quantity
            setCartProductRes(p => ({...p, newProduct}))
            dispatch(setError(res.data.message))
        } catch (error) {
            console.log(error)
            dispatch(setError(error.response.data.message))
        }
    }

    //get use address modal
    const [addmodalisOpen, setaddmodalIsOpen] = useState(false)

    const [isCheckoutLoading, setischeckoutLoading] = useState(false);
    const userAddress = useSelector(state => state.user.address)   
    const handleCheckout = async () => {

        if(!user) {
            return navigate('/login');
        } 

        // if there is address then continue or set get address popup
        console.log({userAddress})
        if(!userAddress){  //if address is not stored in users local storage then get from db
            try {
                const {data} = await userRequest.get("/api/user/address")
                console.log(data)
                if(!data.ok){
                    return setaddmodalIsOpen(true);
                }
                dispatch(setAddress(data.address))  //setting address wh to redux       
            } catch (error) {
                return setaddmodalIsOpen(true)
            }
        }
console.log("Mihir1");


        setischeckoutLoading(true) 
        if(!window.Razorpay) {
            await addDynamicScript("https://checkout.razorpay.com/v1/checkout.js") //script is not loading at first time dk why so i added this XD
        } 
        console.log("Mihir2");

        const {data:{order}} = await userRequest.post("api/buy/checkout",{
            user:user._id,
            type: "cart",
            userInfo: {
                address: userAddress,
                name: `${user.firstName} ${user.lastName}`,
                email: user.email,
            }
        });
        console.log("MIhir3");
        

        const {data:{key}} = await userRequest.get("api/buy/getkey");
        setischeckoutLoading(false) 

        if(!order || !key){
            return dispatch(setError("error accured while creating order"));
        }
          
        const options = {
          key: key, //reciving key from backend for security purpose
          amount: order.ammount,
          currency: "INR",
          name: `${user.firstName} ${user.lastName}'s Cart`,
          description: `${user.firstName} ${user.lastName}'s Cart includes total ${cartProductRes?.products?.length}`,
          image:
            "https://toppng.com/uploads/preview/astronaut-art-png-jpg-royalty-free-stock-astronauta-dibujo-11562856188offwkk8qo8.png",
          order_id: order.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
          callback_url:
            "https://panara-backend-1.onrender.com/api/buy/paymentVerify",
          prefill: {
            name: `${user.firstName} ${user.lastName}`,
            email: user.email,
            contact: user.number,
          },
          notes: {
            address: "Razorpay Corporate Office",
          },
          theme: {
            color: "#40a0a0",
          },
        };      
        const rzp1 = new window.Razorpay(options);
        rzp1.open();       
        setCartProductRes(null)
    }
    


  return (
    <Container>
      <Announcments />
      <Navbar />
      <Wrapper>
        <Title>Cart</Title>
        {fetchCartLoading ? (
          <Loading />
        ) : cartProductRes?.products.length ? (
          <>
            <Top>
              <Link to="/Home"></Link> <TopButton>Continue Shopping</TopButton>
              <TopTexts>
                <TopText>Shopping ba</TopText>
              </TopTexts>
              <TopButton onClick={handleCheckout} type="filled">
                CheckOut Now
              </TopButton>
            </Top>
            <Bottom>
              <Info>
                {cartProductRes?.products?.map((product) => (
                  <Product key={product.productID}>
                    <DelButton
                      onClick={() => handleDeleteProduct(product.productID)}
                    >
                      <ClearOutlined
                        style={{ fontSize: "40px", color: "#AB2A28" }}
                      />
                    </DelButton>
                    <ProductDeteail
                      onClick={() => navigate(`/product/${product._id}`)}
                    >
                      <Image src={product.img} />
                      <Details>
                        <ProductName>
                          <b>Product:</b> {product.title}
                        </ProductName>
                        <ProductNumber>
                          <b>ID:</b> {product.productID}
                        </ProductNumber>
                        <ProductColor color={product.color} />
                        <ProductSize>
                          <b>Size:</b> {product.size}
                        </ProductSize>
                      </Details>
                    </ProductDeteail>
                    <PriceDeteail>
                      <ProductAmmountContainer>
                        <ValueARButton
                          onClick={() =>
                            handleProductQuantityChange(
                              product.productID,
                              --product.quantity
                            )
                          }
                        >
                          <Remove />
                        </ValueARButton>
                        <ProductAmmount>{product.quantity}</ProductAmmount>
                        <ValueARButton
                          onClick={() =>
                            handleProductQuantityChange(
                              product.productID,
                              ++product.quantity
                            )
                          }
                        >
                          <Add />
                        </ValueARButton>
                      </ProductAmmountContainer>
                      <ProductPrice>{product.price}</ProductPrice>
                    </PriceDeteail>
                  </Product>
                ))}
                <Hr />
              </Info>
              <Summary>
                <SummaryTitle>Products</SummaryTitle>
                {cartProductRes?.products?.map((product) => (
                  <SummaryItem key={product._id}>
                    <SummaryText>{product.title}</SummaryText>
                    <SummaryPrice>
                      {(product.price * product.quantity)?.toFixed(2)}
                    </SummaryPrice>
                  </SummaryItem>
                ))}
                <SummaryItem type="total">
                  <SummaryText>Total</SummaryText>
                  <SummaryPrice>{totalCartPrice?.toFixed(2)}</SummaryPrice>
                </SummaryItem>
                <ButtonWrapper>
                  <Button
                    onClick={handleCheckout}
                    disabled={isCheckoutLoading ? true : false}
                  >
                    Check out
                  </Button>
                </ButtonWrapper>
              </Summary>
            </Bottom>
          </>
        ) : (
          <EmptyCartComponent />
        )}
        <GetUserAddress setModal={setaddmodalIsOpen} isOpen={addmodalisOpen} />
      </Wrapper>
      <NewsLetter />
      <Footer />
    </Container>
  );
}

export default CartPage
